def hp_table(motor_rpm, base_flow, base_height):
    
    found = False  # Flag to break out of loops
    
    
    if motor_rpm == 1450:
        catalog = catalog_1450
    elif motor_rpm == 2900:
        catalog = catalog_2900
    
    for name,idx in catalog.items():
            if found:  # Breaks outer loop if a result is found
                break
            for flow in reversed(idx.iloc[0]):
                try:
                    if flow > base_flow:
                        for height in idx[idx.where(idx == flow).stack().index.tolist()[0][1]]:
                            print(height)
                            if height > base_height:
                                # Print the found result
                                stage = idx.loc[idx.where(idx == height).stack().index.tolist()[0][0]][0]
                                power = idx.loc[idx.where(idx == height).stack().index.tolist()[0][0]][10]
                                
                                return f"{flow}", height, stage, power, name
                                found = True  # Set the flag to break outer loops
                                break
                        if found:
                            break
                except TypeError:
                    pass
            
