def sub_table(base_flow, base_height):
    
    found = False  # Flag to break out of loops
    
    for name,idx in catalog.items():
        if found:  # Breaks outer loop if a result is found
                break
                
        for flow in reversed(idx.iloc[1]):
            try:
                if flow > base_flow:
                    for height in idx[idx.where(idx == flow).stack().index.tolist()[0][1]][3:]:
                        # print("Height:", height)
                        if int(height) > base_height:
                            # print(height)
                            
                            stage = list(idx.loc[idx.where(idx == height).stack().index.tolist()[0][0]])[-1]
                            power = list(idx.loc[idx.where(idx == height).stack().index.tolist()[0][0]])[-4]
                                    
                            return f"{flow}", height, stage, power, name
            except:
                pass
            
